import React, { useEffect, useState } from 'react';

function PoliceStations() {
  const [stations, setStations] = useState([]);
  const [formData, setFormData] = useState({ station_name: '', location: '', contact_number: '' });

  useEffect(() => {
    fetch('http://localhost:5000/api/stations')
      .then(res => res.json())
      .then(data => setStations(data));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://localhost:5000/api/stations', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    })
    .then(res => res.json())
    .then(data => {
      setStations([...stations, { station_id: data.id, ...formData }]);
      setFormData({ station_name: '', location: '', contact_number: '' });
    });
  }

  return (
    <div>
      <h2>Police Stations</h2>
      <form onSubmit={handleSubmit}>
        <input placeholder="Station Name" value={formData.station_name} onChange={e => setFormData({...formData, station_name: e.target.value})} required />
        <input placeholder="Location" value={formData.location} onChange={e => setFormData({...formData, location: e.target.value})} required />
        <input placeholder="Contact Number" value={formData.contact_number} onChange={e => setFormData({...formData, contact_number: e.target.value})} required />
        <button type="submit">Add Station</button>
      </form>
      <table>
        <thead>
          <tr>
            <th>ID</th><th>Name</th><th>Location</th><th>Contact</th>
          </tr>
        </thead>
        <tbody>
          {stations.map(s => (
            <tr key={s.station_id}>
              <td>{s.station_id}</td><td>{s.station_name}</td><td>{s.location}</td><td>{s.contact_number}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default PoliceStations;
