import React, { useEffect, useState } from 'react';

function Officers() {
  const [officers, setOfficers] = useState([]);
  const [stations, setStations] = useState([]);
  const [formData, setFormData] = useState({ name: '', officer_rank: '', station_id: '' });

  useEffect(() => {
    fetch('http://localhost:5000/api/officers')
      .then(res => res.json())
      .then(data => setOfficers(data));

    fetch('http://localhost:5000/api/stations')
      .then(res => res.json())
      .then(data => setStations(data));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://localhost:5000/api/officers', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    })
    .then(res => res.json())
    .then(data => {
      setOfficers([...officers, { officer_id: data.id, ...formData }]);
      setFormData({ name: '', officer_rank: '', station_id: '' });
    });
  }

  return (
    <div>
      <h2>Police Officers</h2>
      <form onSubmit={handleSubmit}>
        <input placeholder="Name" value={formData.name} onChange={e => setFormData({...formData, name: e.target.value})} required />
        <input placeholder="Rank" value={formData.officer_rank} onChange={e => setFormData({...formData, officer_rank: e.target.value})} required />
        <select value={formData.station_id} onChange={e => setFormData({...formData, station_id: e.target.value})} required>
          <option value="">Select Station</option>
          {stations.map(s => (
            <option key={s.station_id} value={s.station_id}>{s.station_name}</option>
          ))}
        </select>
        <button type="submit">Add Officer</button>
      </form>
      <table>
        <thead>
          <tr><th>ID</th><th>Name</th><th>Rank</th><th>Station</th></tr>
        </thead>
        <tbody>
          {officers.map(o => (
            <tr key={o.officer_id}>
              <td>{o.officer_id}</td><td>{o.name}</td><td>{o.officer_rank}</td><td>{stations.find(s => s.station_id === o.station_id)?.station_name}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Officers;
