import React, { useState, useEffect } from 'react';

function PoliceStations() {
  const [stations, setStations] = useState([]);
  const [name, setName] = useState('');
  const [location, setLocation] = useState('');
  const [contact, setContact] = useState('');

  useEffect(() => {
    fetch('http://localhost:5000/api/stations')
      .then(res => res.json())
      .then(data => setStations(data));
  }, []);

  const handleAdd = (e) => {
    e.preventDefault();
    fetch('http://localhost:5000/api/stations', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ station_name: name, location, contact_number: contact })
    })
    .then(res => res.json())
    .then(data => {
      setStations([...stations, { station_id: data.id, station_name: name, location, contact_number: contact }]);
      setName(''); setLocation(''); setContact('');
    });
  }

  return (
    <div>
      <h2>Police Stations</h2>
      <form onSubmit={handleAdd}>
        <input placeholder="Name" value={name} onChange={e => setName(e.target.value)} required />
        <input placeholder="Location" value={location} onChange={e => setLocation(e.target.value)} required />
        <input placeholder="Contact" value={contact} onChange={e => setContact(e.target.value)} required />
        <button type="submit">Add Station</button>
      </form>
      <table border="1">
        <thead>
          <tr>
            <th>ID</th><th>Name</th><th>Location</th><th>Contact</th>
          </tr>
        </thead>
        <tbody>
          {stations.map(s => (
            <tr key={s.station_id}>
              <td>{s.station_id}</td>
              <td>{s.station_name}</td>
              <td>{s.location}</td>
              <td>{s.contact_number}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default PoliceStations;
