import React, { useEffect, useState } from 'react';

function CrimeReports() {
  const [reports, setReports] = useState([]);
  const [crimes, setCrimes] = useState([]);
  const [criminals, setCriminals] = useState([]);
  const [victims, setVictims] = useState([]);
  const [formData, setFormData] = useState({ crime_id: '', criminal_id: '', victim_id: '', status: '', remarks: '' });

  useEffect(() => {
    fetch('http://localhost:5000/api/reports').then(res => res.json()).then(data => setReports(data));
    fetch('http://localhost:5000/api/crimes').then(res => res.json()).then(data => setCrimes(data));
    fetch('http://localhost:5000/api/criminals').then(res => res.json()).then(data => setCriminals(data));
    fetch('http://localhost:5000/api/victims').then(res => res.json()).then(data => setVictims(data));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://localhost:5000/api/reports', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    })
    .then(res => res.json())
    .then(data => {
      setReports([...reports, { report_id: data.id, ...formData }]);
      setFormData({ crime_id: '', criminal_id: '', victim_id: '', status: '', remarks: '' });
    });
  }

  return (
    <div>
      <h2>Crime Reports</h2>
      <form onSubmit={handleSubmit}>
        <select value={formData.crime_id} onChange={e => setFormData({...formData, crime_id: e.target.value})} required>
          <option value="">Select Crime</option>
          {crimes.map(c => (<option key={c.crime_id} value={c.crime_id}>{c.crime_type}</option>))}
        </select>
        <select value={formData.criminal_id} onChange={e => setFormData({...formData, criminal_id: e.target.value})} required>
          <option value="">Select Criminal</option>
          {criminals.map(c => (<option key={c.criminal_id} value={c.criminal_id}>{c.name}</option>))}
        </select>
        <select value={formData.victim_id} onChange={e => setFormData({...formData, victim_id: e.target.value})} required>
          <option value="">Select Victim</option>
          {victims.map(v => (<option key={v.victim_id} value={v.victim_id}>{v.name}</option>))}
        </select>
        <input placeholder="Status" value={formData.status} onChange={e => setFormData({...formData, status: e.target.value})} required />
        <input placeholder="Remarks" value={formData.remarks} onChange={e => setFormData({...formData, remarks: e.target.value})} required />
        <button type="submit">Add Report</button>
      </form>
      <table>
        <thead>
          <tr><th>ID</th><th>Crime</th><th>Criminal</th><th>Victim</th><th>Status</th><th>Remarks</th></tr>
        </thead>
        <tbody>
          {reports.map(r => (
            <tr key={r.report_id}>
              <td>{r.report_id}</td>
              <td>{crimes.find(c => c.crime_id === r.crime_id)?.crime_type}</td>
              <td>{criminals.find(c => c.criminal_id === r.criminal_id)?.name}</td>
              <td>{victims.find(v => v.victim_id === r.victim_id)?.name}</td>
              <td>{r.status}</td>
              <td>{r.remarks}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default CrimeReports;
