import React, { useState, useEffect } from 'react';

function Criminals() {
  const [criminals, setCriminals] = useState([]);
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [gender, setGender] = useState('');
  const [address, setAddress] = useState('');
  const [crimeHistory, setCrimeHistory] = useState('');

  useEffect(() => {
    fetch('http://localhost:5000/api/criminals')
      .then(res => res.json())
      .then(data => setCriminals(data));
  }, []);

  const handleAdd = (e) => {
    e.preventDefault();
    fetch('http://localhost:5000/api/criminals', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ name, age: parseInt(age), gender, address, crime_history: crimeHistory })
    })
    .then(res => res.json())
    .then(data => {
      setCriminals([...criminals, { criminal_id: data.id, name, age, gender, address, crime_history: crimeHistory }]);
      setName(''); setAge(''); setGender(''); setAddress(''); setCrimeHistory('');
    });
  }

  return (
    <div>
      <h2>Criminals</h2>
      <form onSubmit={handleAdd}>
        <input placeholder="Name" value={name} onChange={e => setName(e.target.value)} required />
        <input placeholder="Age" value={age} onChange={e => setAge(e.target.value)} required />
        <input placeholder="Gender" value={gender} onChange={e => setGender(e.target.value)} required />
        <input placeholder="Address" value={address} onChange={e => setAddress(e.target.value)} required />
        <input placeholder="Crime History" value={crimeHistory} onChange={e => setCrimeHistory(e.target.value)} />
        <button type="submit">Add Criminal</button>
      </form>
      <table border="1">
        <thead>
          <tr>
            <th>ID</th><th>Name</th><th>Age</th><th>Gender</th><th>Address</th><th>Crime History</th>
          </tr>
        </thead>
        <tbody>
          {criminals.map(c => (
            <tr key={c.criminal_id}>
              <td>{c.criminal_id}</td><td>{c.name}</td><td>{c.age}</td><td>{c.gender}</td><td>{c.address}</td><td>{c.crime_history}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Criminals;
