import React, { useEffect, useState } from 'react';

function Crimes() {
  const [crimes, setCrimes] = useState([]);
  const [stations, setStations] = useState([]);
  const [officers, setOfficers] = useState([]);
  const [formData, setFormData] = useState({ crime_type: '', date_of_crime: '', location: '', station_id: '', officer_id: '' });

  useEffect(() => {
    fetch('http://localhost:5000/api/crimes')
      .then(res => res.json())
      .then(data => setCrimes(data));

    fetch('http://localhost:5000/api/stations')
      .then(res => res.json())
      .then(data => setStations(data));

    fetch('http://localhost:5000/api/officers')
      .then(res => res.json())
      .then(data => setOfficers(data));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://localhost:5000/api/crimes', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    })
    .then(res => res.json())
    .then(data => {
      setCrimes([...crimes, { crime_id: data.id, ...formData }]);
      setFormData({ crime_type: '', date_of_crime: '', location: '', station_id: '', officer_id: '' });
    });
  }

  return (
    <div>
      <h2>Crimes</h2>
      <form onSubmit={handleSubmit}>
        <input placeholder="Crime Type" value={formData.crime_type} onChange={e => setFormData({...formData, crime_type: e.target.value})} required />
        <input type="date" value={formData.date_of_crime} onChange={e => setFormData({...formData, date_of_crime: e.target.value})} required />
        <input placeholder="Location" value={formData.location} onChange={e => setFormData({...formData, location: e.target.value})} required />
        <select value={formData.station_id} onChange={e => setFormData({...formData, station_id: e.target.value})} required>
          <option value="">Select Station</option>
          {stations.map(s => (<option key={s.station_id} value={s.station_id}>{s.station_name}</option>))}
        </select>
        <select value={formData.officer_id} onChange={e => setFormData({...formData, officer_id: e.target.value})} required>
          <option value="">Select Officer</option>
          {officers.map(o => (<option key={o.officer_id} value={o.officer_id}>{o.name}</option>))}
        </select>
        <button type="submit">Add Crime</button>
      </form>
      <table>
        <thead>
          <tr><th>ID</th><th>Type</th><th>Date</th><th>Location</th><th>Station</th><th>Officer</th></tr>
        </thead>
        <tbody>
          {crimes.map(c => (
            <tr key={c.crime_id}>
              <td>{c.crime_id}</td>
              <td>{c.crime_type}</td>
              <td>{c.date_of_crime}</td>
              <td>{c.location}</td>
              <td>{stations.find(s => s.station_id === c.station_id)?.station_name}</td>
              <td>{officers.find(o => o.officer_id === c.officer_id)?.name}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Crimes;
