import React, { useEffect, useState } from 'react';

function Victims() {
  const [victims, setVictims] = useState([]);
  const [formData, setFormData] = useState({ name: '', age: '', gender: '', address: '' });

  useEffect(() => {
    fetch('http://localhost:5000/api/victims')
      .then(res => res.json())
      .then(data => setVictims(data));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://localhost:5000/api/victims', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    })
    .then(res => res.json())
    .then(data => {
      setVictims([...victims, { victim_id: data.id, ...formData }]);
      setFormData({ name: '', age: '', gender: '', address: '' });
    });
  }

  return (
    <div>
      <h2>Victims</h2>
      <form onSubmit={handleSubmit}>
        <input placeholder="Name" value={formData.name} onChange={e => setFormData({...formData, name: e.target.value})} required />
        <input placeholder="Age" type="number" value={formData.age} onChange={e => setFormData({...formData, age: e.target.value})} required />
        <select value={formData.gender} onChange={e => setFormData({...formData, gender: e.target.value})} required>
          <option value="">Select Gender</option>
          <option value="Male">Male</option>
          <option value="Female">Female</option>
        </select>
        <input placeholder="Address" value={formData.address} onChange={e => setFormData({...formData, address: e.target.value})} required />
        <button type="submit">Add Victim</button>
      </form>
      <table>
        <thead>
          <tr><th>ID</th><th>Name</th><th>Age</th><th>Gender</th><th>Address</th></tr>
        </thead>
        <tbody>
          {victims.map(v => (
            <tr key={v.victim_id}>
              <td>{v.victim_id}</td><td>{v.name}</td><td>{v.age}</td><td>{v.gender}</td><td>{v.address}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Victims;
