import React, { useEffect, useState } from 'react';

function Criminals() {
  const [criminals, setCriminals] = useState([]);
  const [formData, setFormData] = useState({ name: '', age: '', gender: '', address: '', crime_history: '' });

  useEffect(() => {
    fetch('http://localhost:5000/api/criminals')
      .then(res => res.json())
      .then(data => setCriminals(data));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://localhost:5000/api/criminals', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    })
    .then(res => res.json())
    .then(data => {
      setCriminals([...criminals, { criminal_id: data.id, ...formData }]);
      setFormData({ name: '', age: '', gender: '', address: '', crime_history: '' });
    });
  }

  return (
    <div>
      <h2>Criminals</h2>
      <form onSubmit={handleSubmit}>
        <input placeholder="Name" value={formData.name} onChange={e => setFormData({...formData, name: e.target.value})} required />
        <input placeholder="Age" type="number" value={formData.age} onChange={e => setFormData({...formData, age: e.target.value})} required />
        <select value={formData.gender} onChange={e => setFormData({...formData, gender: e.target.value})} required>
          <option value="">Select Gender</option>
          <option value="Male">Male</option>
          <option value="Female">Female</option>
        </select>
        <input placeholder="Address" value={formData.address} onChange={e => setFormData({...formData, address: e.target.value})} required />
        <input placeholder="Crime History" value={formData.crime_history} onChange={e => setFormData({...formData, crime_history: e.target.value})} required />
        <button type="submit">Add Criminal</button>
      </form>
      <table>
        <thead>
          <tr><th>ID</th><th>Name</th><th>Age</th><th>Gender</th><th>Address</th><th>Crime History</th></tr>
        </thead>
        <tbody>
          {criminals.map(c => (
            <tr key={c.criminal_id}>
              <td>{c.criminal_id}</td><td>{c.name}</td><td>{c.age}</td><td>{c.gender}</td><td>{c.address}</td><td>{c.crime_history}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Criminals;
